export const getAllPhrases = (): Phrase[] => {
  return situations.flatMap(s => s.phrases);
};

export const getSituationById = (id: SituationCategory): Situation | undefined => {
  return situations.find(s => s.id === id);
};

const normalizePinyin = (text: string): string => {
  const toneMap: { [key: string]: string } = {
    'ā': 'a', 'á': 'a', 'ǎ': 'a', 'à': 'a',
    'ē': 'e', 'é': 'e', 'ě': 'e', 'è': 'e',
    'ī': 'i', 'í': 'i', 'ǐ': 'i', 'ì': 'i',
    'ō': 'o', 'ó': 'o', 'ǒ': 'o', 'ò': 'o',
    'ū': 'u', 'ú': 'u', 'ǔ': 'u', 'ù': 'u',
    'ǖ': 'v', 'ǘ': 'v', 'ǚ': 'v', 'ǜ': 'v', 'ü': 'v',
  };
  return text.toLowerCase().split('').map(char => toneMap[char] || char).join('');
};

export const searchPhrases = (query: string): Phrase[] => {
  const lowerQuery = query.toLowerCase();
  const normalizedQuery = normalizePinyin(query);
  
  return getAllPhrases().filter(phrase => {
    if (phrase.english.toLowerCase().includes(lowerQuery)) return true;
    if (phrase.pinyin.toLowerCase().includes(lowerQuery)) return true;
    if (normalizePinyin(phrase.pinyin).includes(normalizedQuery)) return true;
    if (phrase.chinese.includes(query)) return true;
    if (phrase.taiwanChinese?.includes(query)) return true;
    if (phrase.taiwanPinyin) {
      if (phrase.taiwanPinyin.toLowerCase().includes(lowerQuery)) return true;
      if (normalizePinyin(phrase.taiwanPinyin).includes(normalizedQuery)) return true;
    }
    return false;
  });
};
